async def rasi_prediction(update: Update, context: CallbackContext) -> None:
    user_text = update.message.text.strip()

    if user_text in rasi_predictions:
        prediction = random.choice(rasi_predictions[user_text])  # ЁЯФе Always Random Response
        await update.message.reply_text(f"ЁЯФо **{user_text} рокро▓ройрпН:** {prediction}")
    else:
        await update.message.reply_text("тЪая╕П родропро╡рпБроЪрпЖропрпНродрпБ роЪро░ро┐ропро╛рой ро░ро╛роЪро┐ рокрпЖропро░рпИ родрооро┐ро┤ро┐ро▓рпН роЙро│рпНро│ро┐роЯро╡рпБроорпН! (Ex: роорпЗро╖роорпН, ро░ро┐ро╖рокроорпН, рооро┐родрпБройроорпН...)")

def main():
    app = Application.builder().token(TOKEN).build()

    # тЬЕ Command Handlers
    app.add_handler(CommandHandler("start", start))  # /start command Handle
    app.add_handler(MessageHandler(filters.TEXT & ~filters.COMMAND, rasi_prediction))  # User Message Handle

    print("тЬЕ Astrology Bot Running...")

    # Bot Polling Start
    app.run_polling()

